import React, { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { LANGUAGES } from "../constants/langs.ts";

const Curriculum = () => {
    const { i18n, t } = useTranslation();
    const [formData, setFormData] = useState({
        firstName: "",
        lastName: "",
        experience: "",
        education: "",
        skills: ""
    });

    useEffect(() => {
        // Al cargar la página, restaura el idioma seleccionado desde el localStorage
        const selectedLanguage = localStorage.getItem("selectedLanguage");
        if (selectedLanguage) {
            i18n.changeLanguage(selectedLanguage);
        }
    }, []); // El efecto se ejecuta solo una vez al cargar la página

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
        setFormData({
            firstName: "",
            lastName: "",
            experience: "",
            education: "",
            skills: ""
        });
    };

    const handleLanguageChange = (event) => {
        const selectedLanguage = event.target.value;
        i18n.changeLanguage(selectedLanguage);
        // Almacena el idioma seleccionado en el localStorage
        localStorage.setItem("selectedLanguage", selectedLanguage);
    };

    return (
        <div style={{ backgroundColor: "#f2f2f2", padding: "20px", borderRadius: "8px", maxWidth: "450px", margin: "0 auto", textAlign: "left" }}>
            <h1>{t("curriculum")}</h1>
            <div>
                {/* Selector de idioma */}
                <label htmlFor="languageSelect">Select language:</label>
                <select id="languageSelect" onChange={handleLanguageChange} value={i18n.language}>
                    {LANGUAGES.map(({ code, label }) => (
                        <option key={code} value={code}>
                            {label}
                        </option>
                    ))}
                </select>
            </div>
            <form onSubmit={handleSubmit}>
                {/* Aquí va el resto del formulario */}
            </form>
        </div>
    );
};

export default Curriculum;
