import React, { useState, useEffect } from "react";  
import { useTranslation } from "react-i18next";
import { LANGUAGES } from "../../constants/langs.ts";
import { faTwitter, faFacebook, faInstagram } from '@fortawesome/free-brands-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const Footer = () => {
    const { i18n, t } = useTranslation();
    const [visitCount, setVisitCount] = useState(0);

    useEffect(() => {
        // Incrementa el contador de visitas al montar el componente
        setVisitCount(prevCount => prevCount + 1);

        // Retorna una función para decrementar el contador cuando el componente se desmonte
        return () => {
            setVisitCount(prevCount => prevCount - 1);
        };
    }, []); // El segundo argumento de useEffect como un array vacío asegura que el efecto solo se ejecute una vez (al montar/desmontar)

    return (
        <div>
            <h1>{t("footer")}</h1>
            <p>La página ha sido visitada {visitCount} veces.</p>
            <div>
                <FontAwesomeIcon icon={faTwitter} size="2x" style={{ marginRight: "10px" }} />
                <FontAwesomeIcon icon={faFacebook} size="2x" style={{ marginRight: "10px" }} />
                <FontAwesomeIcon icon={faInstagram} size="2x" style={{ marginRight: "10px" }} />
            </div>
        </div>
    );
};

export default Footer;
