import React from "react";  
import { useTranslation } from "react-i18next";
import { LANGUAGES } from "../../constants/langs.ts";

const HeaderNav = () => {
    const { i18n, t} = useTranslation();
    
    const onChangeLang = (event) => {
        const selectedLanguage = event.target.value;
        i18n.changeLanguage(selectedLanguage);
    };

    return (
        <div>
            <h1>{t("titleHeader")}</h1>
            <div className="flex items-center"> {/* Contenedor para alinear label y select */}
                <label htmlFor="languageSelect" className="absolute top-4 right-12">Select language:</label> {/* Etiqueta asociada al select */}
                <select
                    id="languageSelect" // Añade un ID para asociar la etiqueta
                    defaultValue={i18n.language}
                    onChange={onChangeLang}
                    className="absolute top-2 right-2"
                >
                    {LANGUAGES.map(({ code, label }) => (                                
                        <option key={code} value={code}>
                            {label}
                        </option>
                    ))}
                </select>
            </div>
            <ul className="list-none flex">
                <li className="mr-4">
                    <a href="/inicio" className="no-underline text-black font-bold text-base">{t("inicioNav")}</a> 
                </li>
                <li className="mr-4">
                    <a href="/portfolio" className="no-underline text-black font-bold text-base">{t("portfolioNav")}</a> 
                </li>
                <li className="mr-4">
                    <a href="/contacto" className="no-underline text-black font-bold text-base">{t("contactoNav")}</a>
                </li>
                <li className="mr-4">
                    <a href="/curriculum" className="no-underline text-black font-bold text-base">{t("curriculumNav")}</a> 
                </li>
                <li className="mr-4">
                    <a href="/proyectos" className="no-underline text-black font-bold text-base">{t("proyectosNav")}</a> 
                </li>
                <li>
                    <a href="/servicios" className="no-underline text-black font-bold text-base">{t("serviciosNav")}</a> 
                </li>
            </ul>
        </div>
    )
}

export default HeaderNav;
