import React, { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { LANGUAGES } from "../constants/langs.ts";

const Curriculum = () => {
    const { i18n, t } = useTranslation();
    const [formData, setFormData] = useState({
        firstName: "",
        lastName: "",
        experience: "",
        education: "",
        skills: ""
    });

    useEffect(() => {
        // Al cargar la página, restaura la selección del idioma desde el almacenamiento local
        const storedLanguage = localStorage.getItem('language');
        if (storedLanguage) {
            i18n.changeLanguage(storedLanguage);
        }
    }, []); // El efecto se ejecuta solo una vez al cargar la página

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
        setFormData({
            firstName: "",
            lastName: "",
            experience: "",
            education: "",
            skills: ""
        });
    };

    const handleLanguageChange = (event) => {
        const selectedLanguage = event.target.value;
        // Cambia el idioma y guarda la selección en el almacenamiento local
        i18n.changeLanguage(selectedLanguage);
        localStorage.setItem('language', selectedLanguage);
    };

    return (
        <div className="bg-gray-200 py-8 px-4 sm:px-6 lg:px-8 max-w-3xl mx-auto rounded-lg">
            <h1 className="text-2xl font-semibold mb-4">{t("curriculum")}</h1>
            <div className="mb-4">
                <label htmlFor="languageSelect" className="block text-gray-700">{t("selectLanguage")}</label>
                <select
                    id="languageSelect"
                    value={i18n.language}
                    onChange={handleLanguageChange}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                >
                    {LANGUAGES.map(({ code, label }) => (
                        <option key={code} value={code}>
                            {label}
                        </option>
                    ))}
                </select>
            </div>
            <form onSubmit={handleSubmit}>
                {/* Aquí va el resto del formulario */}
            </form>
        </div>
    );
};

export default Curriculum;
