import React from "react";  
import { useTranslation } from "react-i18next";
import { LANGUAGES } from "../../constants/langs.ts";

const HeaderNav = () => {
    const { i18n, t} = useTranslation();
    
    const onChangeLang = (event) => {
        const selectedLanguage = event.target.value;
        i18n.changeLanguage(selectedLanguage);
    };

    return (
        <nav className="flex items-center justify-between flex-wrap bg-gray-800 p-6">
            <div className="flex items-center flex-shrink-0 text-white mr-6">
                <span className="font-semibold text-xl tracking-tight">{t("titleHeader")}</span>
            </div>
            <div className="w-full block flex-grow lg:flex lg:items-center lg:w-auto">
                <div className="text-sm lg:flex-grow">
                    <ul className="list-none lg:flex lg:justify-end">
                        {LANGUAGES.map(({ code, label }) => (                                
                            <li key={code} className="mr-4">
                                <a href="/" className="block mt-4 lg:inline-block lg:mt-0 text-white hover:text-gray-200">
                                    {label}
                                </a>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </nav>
    )
}

export default HeaderNav;
